(function ($) {
    var settings7 = new Array();
    var group7 = new Array();
    var group8 = new Array();
    var onSort7 = new Array();
    $.configureBoxes7 = function (options) {
        var index = settings7.push({
            box7View: 'box7View',
            box7Storage: 'box7Storage',
            box7Filter: 'box7Filter',
            box7Clear: 'box7Clear',
            box7Counter: 'box7Counter',
            box8View: 'box8View',
            box8Storage: 'box8Storage',
            box8Filter: 'box8Filter',
            box8Clear: 'box8Clear',
            box8Counter: 'box8Counter',
            to7: 'to7',
            allTo7: 'allTo7',
            to8: 'to8',
            allTo8: 'allTo8',
            transferMode: 'move',
            sortBy: 'text',
            useFilters: true,
            useCounters: true,
            useSorting: false,
            selectOnSubmit: true
        });
        index--;
        $.extend(settings7[index], options);
        group7.push({
            view: settings7[index].box7View,
            storage: settings7[index].box7Storage,
            filter: settings7[index].box7Filter,
            clear: settings7[index].box7Clear,
            counter: settings7[index].box7Counter,
            index: index
        });
        group8.push({
            view: settings7[index].box8View,
            storage: settings7[index].box8Storage,
            filter: settings7[index].box8Filter,
            clear: settings7[index].box8Clear,
            counter: settings7[index].box8Counter,
            index: index
        });
        if (settings7[index].sortBy == 'text') {
            onSort7.push(function (a, b) {
                var aVal = a.text.toLowerCase();
                var bVal = b.text.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        } else {
            onSort7.push(function (a, b) {
                var aVal = a.value.toLowerCase();
                var bVal = b.value.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        }
        if (settings7[index].useFilters) {
            $('#' + group7[index].filter).keyup(function () {
                Filter(group7[index]);
            });
            $('#' + group8[index].filter).keyup(function () {
                Filter(group8[index]);
            });
            $('#' + group7[index].clear).click(function () {
                ClearFilter(group7[index]);
            });
            $('#' + group8[index].clear).click(function () {
                ClearFilter(group8[index]);
            });
        }
        if (IsMoveMode(settings7[index])) {
            $('#' + group8[index].view).dblclick(function () {
                MoveSelected(group8[index], group7[index]);
            });
            $('#' + settings7[index].to7).click(function () {
                MoveSelected(group8[index], group7[index]);
            });
            $('#' + settings7[index].allTo7).click(function () {
                MoveAll(group8[index], group7[index]);
            });
        } else {
            $('#' + group8[index].view).dblclick(function () {
                RemoveSelected(group8[index], group7[index]);
            });
            $('#' + settings7[index].to7).click(function () {
                RemoveSelected(group8[index], group7[index]);
            });
            $('#' + settings7[index].allTo7).click(function () {
                RemoveAll(group8[index], group7[index]);
            });
        }
        $('#' + group7[index].view).dblclick(function () {
            MoveSelected(group7[index], group8[index]);
        });
        $('#' + settings7[index].to8).click(function () {
            MoveSelected(group7[index], group8[index]);
        });
        $('#' + settings7[index].allTo8).click(function () {
            MoveAll(group7[index], group8[index]);
        });
        if (settings7[index].useCounters) {
            UpdateLabel(group7[index]);
            UpdateLabel(group8[index]);
        }
        if (settings7[index].useSorting) {
            SortOptions(group7[index]);
            SortOptions(group8[index]);
        }
        $('#' + group7[index].storage + ',#' + group8[index].storage).css('display', 'none');
        if (settings7[index].selectOnSubmit) {
            $('#' + settings7[index].box8View).closest('form').submit(function () {
                $('#' + settings7[index].box8View).children('option').attr('selected', 'selected');
            });
        }
    };

    function UpdateLabel(group) {
        var showingCount = $("#" + group.view + " option").size();
        var hiddenCount = $("#" + group.storage + " option").size();
        $("#" + group.counter).text('Showing ' + showingCount + ' of ' + (showingCount + hiddenCount));
    }

    function Filter(group) {
        var index = group.index;
        var filterLower;
        if (settings7[index].useFilters) {
            filterLower = $('#' + group.filter).val().toString().toLowerCase();
        } else {
            filterLower = '';
        }
        $('#' + group.view + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) == -1;
        }).appendTo('#' + group.storage);
        $('#' + group.storage + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) != -1;
        }).appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings7[index].useSorting) {
            SortOptions(group);
        }
        if (settings7[index].useCounters) {
            UpdateLabel(group);
        }
    }

    function SortOptions(group) {
        var $toSortOptions = $('#' + group.view + ' option');
        $toSortOptions.sort(onSort7[group.index]);
        $('#' + group.view).empty().append($toSortOptions);
    }

    function MoveSelected(fromGroup, toGroup) {
        if (IsMoveMode(settings7[fromGroup.index])) {
            $('#' + fromGroup.view + ' option:selected').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:selected:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings7[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function MoveAll(fromGroup, toGroup) {
        if (IsMoveMode(settings7[fromGroup.index])) {
            $('#' + fromGroup.view + ' option').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings7[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function RemoveSelected(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.view + ' option:selected').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        $('#' + removeGroup.view + ' option').add('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').appendTo('#' + otherGroup.view);
        Filter(otherGroup);
        if (settings7[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function RemoveAll(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').add('#' + removeGroup.view + ' option').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        Filter(otherGroup);
        if (settings7[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function ClearFilter(group) {
        $('#' + group.filter).val('');
        $('#' + group.storage + ' option').appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings7[group.index].useSorting) {
            SortOptions(group);
        }
        if (settings7[group.index].useCounters) {
            UpdateLabel(group);
        }
    }

    function IsMoveMode(currSettings) {
        return currSettings.transferMode == 'move';
    }
})(jQuery);