(function ($) {
    var settings9 = new Array();
    var group9 = new Array();
    var group10 = new Array();
    var onSort9 = new Array();
    $.configureBoxes9 = function (options) {
        var index = settings9.push({
            box9View: 'box9View',
            box9Storage: 'box9Storage',
            box9Filter: 'box9Filter',
            box9Clear: 'box9Clear',
            box9Counter: 'box9Counter',
            box10View: 'box10View',
            box10Storage: 'box10Storage',
            box10Filter: 'box10Filter',
            box10Clear: 'box10Clear',
            box10Counter: 'box10Counter',
            to9: 'to9',
            allTo9: 'allTo9',
            to10: 'to10',
            allTo10: 'allTo10',
            transferMode: 'move',
            sortBy: 'text',
            useFilters: true,
            useCounters: true,
            useSorting: false,
            selectOnSubmit: true
        });
        index--;
        $.extend(settings9[index], options);
        group9.push({
            view: settings9[index].box9View,
            storage: settings9[index].box9Storage,
            filter: settings9[index].box9Filter,
            clear: settings9[index].box9Clear,
            counter: settings9[index].box9Counter,
            index: index
        });
        group10.push({
            view: settings9[index].box10View,
            storage: settings9[index].box10Storage,
            filter: settings9[index].box10Filter,
            clear: settings9[index].box10Clear,
            counter: settings9[index].box10Counter,
            index: index
        });
        if (settings9[index].sortBy == 'text') {
            onSort9.push(function (a, b) {
                var aVal = a.text.toLowerCase();
                var bVal = b.text.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        } else {
            onSort9.push(function (a, b) {
                var aVal = a.value.toLowerCase();
                var bVal = b.value.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        }
        if (settings9[index].useFilters) {
            $('#' + group9[index].filter).keyup(function () {
                Filter(group9[index]);
            });
            $('#' + group10[index].filter).keyup(function () {
                Filter(group10[index]);
            });
            $('#' + group9[index].clear).click(function () {
                ClearFilter(group9[index]);
            });
            $('#' + group10[index].clear).click(function () {
                ClearFilter(group10[index]);
            });
        }
        if (IsMoveMode(settings9[index])) {
            $('#' + group10[index].view).dblclick(function () {
                MoveSelected(group10[index], group9[index]);
            });
            $('#' + settings9[index].to9).click(function () {
                MoveSelected(group10[index], group9[index]);
            });
            $('#' + settings9[index].allTo9).click(function () {
                MoveAll(group10[index], group9[index]);
            });
        } else {
            $('#' + group10[index].view).dblclick(function () {
                RemoveSelected(group10[index], group9[index]);
            });
            $('#' + settings9[index].to9).click(function () {
                RemoveSelected(group10[index], group9[index]);
            });
            $('#' + settings9[index].allTo9).click(function () {
                RemoveAll(group10[index], group9[index]);
            });
        }
        $('#' + group9[index].view).dblclick(function () {
            MoveSelected(group9[index], group10[index]);
        });
        $('#' + settings9[index].to10).click(function () {
            MoveSelected(group9[index], group10[index]);
        });
        $('#' + settings9[index].allTo10).click(function () {
            MoveAll(group9[index], group10[index]);
        });
        if (settings9[index].useCounters) {
            UpdateLabel(group9[index]);
            UpdateLabel(group10[index]);
        }
        if (settings9[index].useSorting) {
            SortOptions(group9[index]);
            SortOptions(group10[index]);
        }
        $('#' + group9[index].storage + ',#' + group10[index].storage).css('display', 'none');
        if (settings9[index].selectOnSubmit) {
            $('#' + settings9[index].box10View).closest('form').submit(function () {
                $('#' + settings9[index].box10View).children('option').attr('selected', 'selected');
            });
        }
    };

    function UpdateLabel(group) {
        var showingCount = $("#" + group.view + " option").size();
        var hiddenCount = $("#" + group.storage + " option").size();
        $("#" + group.counter).text('Showing ' + showingCount + ' of ' + (showingCount + hiddenCount));
    }

    function Filter(group) {
        var index = group.index;
        var filterLower;
        if (settings9[index].useFilters) {
            filterLower = $('#' + group.filter).val().toString().toLowerCase();
        } else {
            filterLower = '';
        }
        $('#' + group.view + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) == -1;
        }).appendTo('#' + group.storage);
        $('#' + group.storage + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) != -1;
        }).appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings9[index].useSorting) {
            SortOptions(group);
        }
        if (settings9[index].useCounters) {
            UpdateLabel(group);
        }
    }

    function SortOptions(group) {
        var $toSortOptions = $('#' + group.view + ' option');
        $toSortOptions.sort(onSort9[group.index]);
        $('#' + group.view).empty().append($toSortOptions);
    }

    function MoveSelected(fromGroup, toGroup) {
        if (IsMoveMode(settings9[fromGroup.index])) {
            $('#' + fromGroup.view + ' option:selected').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:selected:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings9[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function MoveAll(fromGroup, toGroup) {
        if (IsMoveMode(settings9[fromGroup.index])) {
            $('#' + fromGroup.view + ' option').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings9[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function RemoveSelected(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.view + ' option:selected').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        $('#' + removeGroup.view + ' option').add('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').appendTo('#' + otherGroup.view);
        Filter(otherGroup);
        if (settings9[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function RemoveAll(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').add('#' + removeGroup.view + ' option').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        Filter(otherGroup);
        if (settings9[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function ClearFilter(group) {
        $('#' + group.filter).val('');
        $('#' + group.storage + ' option').appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings9[group.index].useSorting) {
            SortOptions(group);
        }
        if (settings9[group.index].useCounters) {
            UpdateLabel(group);
        }
    }

    function IsMoveMode(currSettings) {
        return currSettings.transferMode == 'move';
    }
})(jQuery);