(function ($) {
    var settings13 = new Array();
    var group13 = new Array();
    var group14 = new Array();
    var onSort13 = new Array();
    $.configureBoxes13 = function (options) {
        var index = settings13.push({
            box13View: 'box13View',
            box13Storage: 'box13Storage',
            box13Filter: 'box13Filter',
            box13Clear: 'box13Clear',
            box13Counter: 'box13Counter',
            box14View: 'box14View',
            box14Storage: 'box14Storage',
            box14Filter: 'box14Filter',
            box14Clear: 'box14Clear',
            box14Counter: 'box14Counter',
            to13: 'to13',
            allTo13: 'allTo13',
            to14: 'to14',
            allTo14: 'allTo14',
            transferMode: 'move',
            sortBy: 'text',
            useFilters: true,
            useCounters: true,
            useSorting: false,
            selectOnSubmit: true
        });
        index--;
        $.extend(settings13[index], options);
        group13.push({
            view: settings13[index].box13View,
            storage: settings13[index].box13Storage,
            filter: settings13[index].box13Filter,
            clear: settings13[index].box13Clear,
            counter: settings13[index].box13Counter,
            index: index
        });
        group14.push({
            view: settings13[index].box14View,
            storage: settings13[index].box14Storage,
            filter: settings13[index].box14Filter,
            clear: settings13[index].box14Clear,
            counter: settings13[index].box14Counter,
            index: index
        });
        if (settings13[index].sortBy == 'text') {
            onSort13.push(function (a, b) {
                var aVal = a.text.toLowerCase();
                var bVal = b.text.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        } else {
            onSort13.push(function (a, b) {
                var aVal = a.value.toLowerCase();
                var bVal = b.value.toLowerCase();
                if (aVal < bVal) {
                    return -1;
                }
                if (aVal > bVal) {
                    return 1;
                }
                return 0;
            });
        }
        if (settings13[index].useFilters) {
            $('#' + group13[index].filter).keyup(function () {
                Filter(group13[index]);
            });
            $('#' + group14[index].filter).keyup(function () {
                Filter(group14[index]);
            });
            $('#' + group13[index].clear).click(function () {
                ClearFilter(group13[index]);
            });
            $('#' + group14[index].clear).click(function () {
                ClearFilter(group14[index]);
            });
        }
        if (IsMoveMode(settings13[index])) {
            $('#' + group14[index].view).dblclick(function () {
                MoveSelected(group14[index], group13[index]);
            });
            $('#' + settings13[index].to13).click(function () {
                MoveSelected(group14[index], group13[index]);
            });
            $('#' + settings13[index].allTo13).click(function () {
                MoveAll(group14[index], group13[index]);
            });
        } else {
            $('#' + group14[index].view).dblclick(function () {
                RemoveSelected(group14[index], group13[index]);
            });
            $('#' + settings13[index].to13).click(function () {
                RemoveSelected(group14[index], group13[index]);
            });
            $('#' + settings13[index].allTo13).click(function () {
                RemoveAll(group14[index], group13[index]);
            });
        }
        $('#' + group13[index].view).dblclick(function () {
            MoveSelected(group13[index], group14[index]);
        });
        $('#' + settings13[index].to14).click(function () {
            MoveSelected(group13[index], group14[index]);
        });
        $('#' + settings13[index].allTo14).click(function () {
            MoveAll(group13[index], group14[index]);
        });
        if (settings13[index].useCounters) {
            UpdateLabel(group13[index]);
            UpdateLabel(group14[index]);
        }
        if (settings13[index].useSorting) {
            SortOptions(group13[index]);
            SortOptions(group14[index]);
        }
        $('#' + group13[index].storage + ',#' + group14[index].storage).css('display', 'none');
        if (settings13[index].selectOnSubmit) {
            $('#' + settings13[index].box14View).closest('form').submit(function () {
                $('#' + settings13[index].box14View).children('option').attr('selected', 'selected');
            });
        }
    };

    function UpdateLabel(group) {
        var showingCount = $("#" + group.view + " option").size();
        var hiddenCount = $("#" + group.storage + " option").size();
        $("#" + group.counter).text('Showing ' + showingCount + ' of ' + (showingCount + hiddenCount));
    }

    function Filter(group) {
        var index = group.index;
        var filterLower;
        if (settings13[index].useFilters) {
            filterLower = $('#' + group.filter).val().toString().toLowerCase();
        } else {
            filterLower = '';
        }
        $('#' + group.view + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) == -1;
        }).appendTo('#' + group.storage);
        $('#' + group.storage + ' option').filter(function (i) {
            var toMatch = $(this).text().toString().toLowerCase();
            return toMatch.indexOf(filterLower) != -1;
        }).appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings13[index].useSorting) {
            SortOptions(group);
        }
        if (settings13[index].useCounters) {
            UpdateLabel(group);
        }
    }

    function SortOptions(group) {
        var $toSortOptions = $('#' + group.view + ' option');
        $toSortOptions.sort(onSort13[group.index]);
        $('#' + group.view).empty().append($toSortOptions);
    }

    function MoveSelected(fromGroup, toGroup) {
        if (IsMoveMode(settings13[fromGroup.index])) {
            $('#' + fromGroup.view + ' option:selected').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:selected:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings13[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function MoveAll(fromGroup, toGroup) {
        if (IsMoveMode(settings13[fromGroup.index])) {
            $('#' + fromGroup.view + ' option').appendTo('#' + toGroup.view);
        } else {
            $('#' + fromGroup.view + ' option:not([class*=copiedOption])').clone().appendTo('#' + toGroup.view).end().end().addClass('copiedOption');
        }
        try {
            $('#' + fromGroup.view + ' option,#' + toGroup.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        Filter(toGroup);
        if (settings13[fromGroup.index].useCounters) {
            UpdateLabel(fromGroup);
        }
    }

    function RemoveSelected(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.view + ' option:selected').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        $('#' + removeGroup.view + ' option').add('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').appendTo('#' + otherGroup.view);
        Filter(otherGroup);
        if (settings13[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function RemoveAll(removeGroup, otherGroup) {
        $('#' + otherGroup.view + ' option.copiedOption').add('#' + otherGroup.storage + ' option.copiedOption').remove();
        try {
            $('#' + removeGroup.storage + ' option').clone().addClass('copiedOption').add('#' + removeGroup.view + ' option').appendTo('#' + otherGroup.view).removeAttr('selected');
        } catch (ex) {
        }
        Filter(otherGroup);
        if (settings13[removeGroup.index].useCounters) {
            UpdateLabel(removeGroup);
        }
    }

    function ClearFilter(group) {
        $('#' + group.filter).val('');
        $('#' + group.storage + ' option').appendTo('#' + group.view);
        try {
            $('#' + group.view + ' option').removeAttr('selected');
        } catch (ex) {
        }
        if (settings13[group.index].useSorting) {
            SortOptions(group);
        }
        if (settings13[group.index].useCounters) {
            UpdateLabel(group);
        }
    }

    function IsMoveMode(currSettings) {
        return currSettings.transferMode == 'move';
    }
})(jQuery);